import { useState } from 'react';
import "./App.css"

export default function Form() {

	// States for registration
	const [name, setName] = useState('');
	const [email, setEmail] = useState('');
	const [phone, ] = useState('');
	const [password, setPassword] = useState('');

	// States for checking the errors
	const [submitted, setSubmitted] = useState(false);
	const [error, setError] = useState(false);

	// Handling the name change
	const handleName = (e) => {
		setName(e.target.value);
		setSubmitted(false);
	};

	// Handling the email change
	const handleEmail = (e) => {
		setEmail(e.target.value);
		setSubmitted(false);
	};

	// Handling the password change
	const handlePassword = (e) => {
		setPassword(e.target.value);
		setSubmitted(false);
	};

	// Handling the form submission
	const handleSubmit = (e) => {
		e.preventDefault();
		if (name === '' || email === '' || password === '') {
			setError(true);
		} else {
			setSubmitted(true);
			setError(false);
		}
	};

	// Showing success message
	const successMessage = () => {
		return (
			<div
				className="success"
				style={{
					display: submitted ? '' : 'none',
				}}>
				<h1 className='success-msg'>User successfully registered!!</h1>
				<h2>Student Data</h2>
				<h2>Name : {name} </h2>
				<h2>email  : {email} </h2>
				<h2>Password : {password} </h2>
			</div>
		);
	};

	// Showing error message if error is true
	const errorMessage = () => {
		return (
			<div
				className="error"
				style={{
					display: error ? '' : 'none',
				}}>
				<h1>Please enter all the fields</h1>
			</div>
		);
	};

	return (

		<div className="form">
			<div className='header'>
				<h1>Student Registration Form 2023</h1>
		</div>
          <form className='form-details'>
				{/* Labels and inputs for form data */}

				<input onChange={handleName} className="input" placeholder=' Full Name'
					value={name} type="text" />


				<input onChange={handleEmail} className="input" placeholder='Email'
					value={email} type="email" />

                <input onChange={handlePhone} className="input" placeholder='Phone Number'
					value={phone} type="number" />					


				<input onChange={handlePassword} className="input" placeholder='Password'
					value={password} type="password" />

				<button onClick={handleSubmit} className="btn"
						type="submit">
					Register
				</button>
			</form>

			{/* Calling to the methods */}
			<div className="msg ">
				{errorMessage()}
				{successMessage()}
			</div>

		</div>
	);
}
