import React, { useState } from 'react';
import './App.css';
function RegistrationForm() {
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isSubmitted,setIsSubmitted] = useState(false);

  const handleSubmit = (e) => {


   console.log('Form submitted:', { firstName, lastName, email, password });
  setIsSubmitted(true);
  };

  const handleclear = (e) => {
    setIsclear
  }

  return (

    <div >

      <div id='section-A'>     
        <h2>Registration Form</h2>
      <form >
        <div>

          <input
            type="text"
            placeholder='First Name '
            value={firstName}
            onChange={(e) => setFirstName(e.target.value)}
            required
          />


        </div>
        <div>

          <input
            type="text"
            placeholder='Last Name '
            value={lastName}
            onChange={(e) => setLastName(e.target.value)}
            required
          />
        </div>
        <div>

          <input
            type="email"
            placeholder='email'
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div>

          <input
            type="password"
            placeholder='Password '
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>  

        <button className='buttonReg' type="submit" onSubmit={handleSubmit}>Enroll Student </button>

       <button className='buttonclr' type="submit" onSubmit={handleSubmit}>Clear</button>

      </form>
     </div>


{isSubmitted == true &&
  <div id='section-B' >
  <h1>data</h1>
  {firstName!==''&&<div>{firstName}</div>} 
  {lastName!==''&&<div>{lastName}</div>} 
  {email!==''&&<div>{email}</div>} 
  {password!==''&&<div>{password}</div>} 

  </div>
}
</div>



);
};
export default RegistrationForm;
